	public double resultado(double valorIndicador, double metaIndicador) {
		EntityManagerFactory factory = HibernateUtil.getEntityManagerFactory();
		EntityManager manager = factory.createEntityManager();
		Query queryValor = manager.createQuery("select i.valorIndicador from Indicador i where i.dataIndicador = :dataIndicador and i.depIndicador = :depIndicador and i.nomeIndicador =:nomeIndicador");
		Query queryMeta = manager.createQuery("select i.metaIndicador from Indicador i where i.dataIndicador = :dataIndicador and i.depIndicador = :depIndicador and i.nomeIndicador =:nomeIndicador");
		queryValor.setParameter("valorIndicador", valorIndicador);
		queryMeta.setParameter("metaIndicador", metaIndicador);
		
	
		
		if( queryValor.getResultList() != null && !queryValor.getResultList().isEmpty()  ) {
			double resultado = ((Double) queryValor.getResultList().get(0) / (Double) queryMeta.getResultList().get(0));
			manager.close();
			return resultado;
		} 
		manager.close();
		return 0;
	}
	
